  Future<List<Slug>> getSlugs() async {
    try {
      // Simula um delay de rede
      await Future.delayed(const Duration(milliseconds: 800));
      
      final Map<String, dynamic> data = json.decode(_localSlugsJson);
      final List<dynamic> slugsData = data['slugs'];
      
      return slugsData.map((json) => Slug.fromJson(json)).toList();
    } catch (e) {
      print('Erro ao carregar slugs locais: $e');
      throw Exception('Falha ao carregar dados locais');
    }
  }

  Future<List<Slug>> searchSlugs(String query) async {
    try {
      // Simula um delay de rede
      await Future.delayed(const Duration(milliseconds: 300));
      
      final Map<String, dynamic> data = json.decode(_localSlugsJson);
      final List<dynamic> slugsData = data['slugs'];
      
      final allSlugs = slugsData.map((json) => Slug.fromJson(json)).toList();
      
      if (query.isEmpty) {
        return allSlugs;
      }
      
      return allSlugs.where((slug) => 
        slug.name.toLowerCase().contains(query.toLowerCase()) ||
        slug.element.toLowerCase().contains(query.toLowerCase()) ||
        slug.abilities.toLowerCase().contains(query.toLowerCase())
      ).toList();
    } catch (e) {
      print('Erro na busca local: $e');
      throw Exception('Falha na busca');
    }
  }
